import { Ionicons } from '@expo/vector-icons';
import React, {Component} from 'react';
import { Image,
  SafeAreaView, 
  Platform, 
  StyleSheet, 
  View, 
  TextInput,
  KeyboardAvoidingView, 
  FlatList,
  Text,
  Button,
  Alert, 
  AsyncStorage } from 'react-native';
import { RectButton, ScrollView } from 'react-native-gesture-handler';
import ItemsRecharge from '../components/ItemsRecharge';

const DATA = [
  {
    id: 'bd7acbea',
    title: "$20",
  },
  {
    id: '3ac68afc',
    title: "$50",
  },
  {
    id: '3ad53abb28ba',
    title: "$100",
  },
  {
    id: '58694a0f',
    title: "$500",
  },
];

export default class RechargeScreen extends Component {
  

  constructor(){
    super()
    this.state = {
      list: ''
    }
    try {
      AsyncStorage.getItem('database_form').then((value) => {
        this.setState({
          list: value
        })
      })
    } catch(err){
      console.log(err)
    }
  }

render(){
  const [value, onChangeText] = React.useState('NUMERO TELEFONICO');

  const [selected, setSelected] = React.useState(new Map());
  console.log(selected);

  const onSelect = React.useCallback(
    title => {
      const newSelected = new Map(selected);
      newSelected.set(title, !selected.get(title));
      setSelected(newSelected);
    },
    console.log([selected]),
  );

  const data = (this.state.list)
  return (
    <KeyboardAvoidingView
    behavior={Platform.OS == "ios" ? "padding" : "padding"}
    style={styles.container}
    >
      
      
        
        <ItemsRecharge/>
            <View style={{padding: 50, alignItems: "center"}}>
              <TextInput
                style={{  
                  height: 50, 
                  textAlign: "center", 
                  width: 300, 
                  borderColor: 'gray', 
                  borderWidth: 2, 
                  borderRadius: 5 }}
                onChangeText={text => onChangeText(text)}
                placeholder='NUMERO TELEFONICO'
                keyboardType='phone-pad'
                
              />
            
              <TextInput
                style={{
                  padding: 8, alignItems: "center", 
                  height: 50, 
                  textAlign: "center", 
                  width: 200, 
                  borderColor: 'gray', 
                  borderWidth: 2, 
                  borderRadius: 5,  }}
                  onChangeText={text => onChangeText(text)}
                  onClear={text => Map('')}
                placeholder='MONTO'
                keyboardType='phone-pad'
                maxLength={4}
/>
                <FlatList
                numColumns={2}
                data={DATA}
                renderItem={({ item }) => (
                      <Item
                        id={item.id}
                        title={item.title}
                        selected={!!selected.get(item.title)}
                        onSelect={onSelect}
                      />                  
                  )
                  
                }
                keyExtractor={item => item.id}
                extraData={selected}
              />
              


              
              <View style={{paddingVertical: 15, width: 200, marginLeft: 100}}>
              <TouchableOpacity
                      style={{
                      marginTop: 20, 
                      width: 100, 
                      height: 50, 
                      borderRadius: 25, 
                      backgroundColor: '#0080FF'}}
                      onPress={enviarRecarga}
                      >
                  <Text style={{color: 'white', 
                  fontSize: 20, 
                  textAlign: 'center', 
                  marginTop: 13}}>Enviar</Text>
                  </TouchableOpacity>
              </View>
            </View>
            
      
      
    </KeyboardAvoidingView>
  );
}
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
  },
  item: {
    padding: 10,
    marginTop: 10,
    width: 100,
    marginRight: 2,
    height: 50,
    borderRadius: 5,
  },
  title: {
    fontSize: 20,
    textAlign: "center",
  },
  welcomeContainer: {
    alignItems: 'center',
    marginTop: 10,
    marginBottom: 20,
  },
});
